.header__user {
  position: relative;
}

.header__user--img {
  height: 44px;
  width: 44px;
  border-radius: 50%;
  overflow: hidden;
  cursor: pointer;
  margin-left: 30px;
}

.header__user--img img {
  height: 100%;
  width: 100%;
  object-fit: cover;
  object-position: top center;
  transform: scale(200%);
  border-radius: 50%;
}

.header__user--info {
  position: absolute;
  right: -30%;
  top: 130%;
  width: 450px;
  background-color: #474646;
  border-radius: 4px;
  /* overflow: hidden; */
  transition: transform 0.3s linear, opacity 0.3s linear, visibility 0.3s linear;
  transform: scale(0%);
  transform-origin: 92% 0% 0;
  opacity: 0;
  visibility: hidden;
  background-image: linear-gradient(rgba(0, 0, 0, 0.8), rgba(0, 0, 0, 0.65)), url('../img/asset/user.jpg');
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
}

.header__user--info__item {
  position: relative;
  /* padding: 0 20px; */
  display: flex;
  justify-content: center;
  align-items: center;
  background-image: none;
  background-color: transparent;
  background-image: none;
}

.header__user--info__link {
  display: flex;
  justify-content: flex-start;
  align-items: center;
  flex: 1;
  height: 100%;
}

.header__user--info__heading {
  height: 100px;
  background-color: transparent;
  background-position: bottom center;
  background-repeat: no-repeat;
  background-size: cover;
  padding: 0 20px;
}

.header__user--info__avatar {
  height: 60px;
  width: 60px;
  border-radius: 50%;
  overflow: hidden;
}

.header__user--info__avatar img {
  height: 100%;
  width: 100%;
  object-fit: cover;
  transform: scale(200%);
  object-position: top center;
}

.header__user--info__desc {
  display: flex;
  justify-content: center;
  flex-direction: column;
  align-items: flex-start;
  flex: 1;
  margin: 0 20px;
  color: var(--text-grey);
}

.header__user--info__desc--item {
  display: flex;
  justify-content: center;
  align-items: center;
}

.header__user--info__desc--item span {
  max-width: 200px;
  display: -webkit-box;
  -webkit-line-clamp: 1;
  -webkit-box-orient: vertical;
  overflow: hidden;
  text-overflow: ellipsis;
  word-break: break-word;
  font-size: 13px;
  line-height: 15px;
  margin-right: 4px;
}

.header__user--info__edit {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 4px 16px;
  border-radius: 50px;
  background-color: #443535;
}

.header__user--info__item--active {
  border-left: 3px solid #ea6016;
}

.header__user--info__item:hover {
  background-image: linear-gradient(to right, #b3704a40, #b3704a40);
  background-position: bottom center;
  background-repeat: no-repeat;
  background-size: cover;
  border-left: 3px solid #ea6016;
}

.header__user--info__item:hover span {
  color: var(--text-white);
}

.header__user--info__item:not(:first-child) .header__user--info__link {
  padding: 12px 20px;
}

.header__user--info__footer {
  background-color: transparent;
  background-position: bottom center;
  background-repeat: no-repeat;
  background-size: cover;
}

.header__user--info__icon {
  margin-right: 20px;
}

.header__user--info::before {
  content: '';
  width: 100%;
  height: 30px;
  background-color: transparent;
  position: absolute;
  top: -30px;
  left: 0;
  z-index: 1000;
}

.header__user:hover .header__user--info {
  transform: scale(100%);
  opacity: 1;
  visibility: visible;
}

a {
  text-decoration: none;
  color: #fff;
}

a:hover {
  color: #fff;
}

.add_cart {
  background-color: transparent;
  border: none;
  outline: none;
  text-transform: uppercase;
  poiter
}

.add_cart:hover,
.feature_btn:hover,
.feature_btn:hover i {
  color: #ea3c2a !important;
}

.input-group-icon.mt-10 .form-select .nice-select.open .list {
  max-height: 300px;
  overflow: hidden scroll;
}

.cart_inner .table tbody tr td .media .d-flex {
  min-height: 200px;
  max-height: 200px;
  height: 200px;
  /* width: 200px; */
}

.cart_inner .table tbody tr td .media .d-flex img {
  height: 100%;
  object-fit: contain;
}

.user {
  width: 44px;
  max-width: 44px;
  height: 44px;
  max-height: 44px;
  border-radius: 50px;
  margin-left: 10px;
  padding: 0 !important;
  cursor: pointer;
}

.user .nav-link {
  padding: 0;
  width: 100%;
  height: 100%;
}

.user .nav-link img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: center;
  border-radius: 50px;
}

.user .dropdown-menu {
  grid-template-columns: repeat(1, 1fr) !important;
}

.user .dropdown-menu .dropdown-item {
  width: 200px;
}

.lslide {
  display: flex;
  justify-content: center;
  align-items: center;
}

.lslide img {
  height: 100%;
  object-fit: contain;
}

.product_image_area {
  padding-top: 150px;
}
.login_part {
  padding-top: 100px;
}
